    void Actualizar() {
        ILista<TarjetaAsociado> tarjetas = biblioteca.getTarjetasAsociado();
        ILista<Solicitud> solicitudes = biblioteca.getSolicitudes();
        ILista<Material> materiales = biblioteca.getMateriales();

        listAsociados.removeAll();
        listSolicitudes.removeAll();
        listMateriales.removeAll();

        for (int i = 0; i < tarjetas.Longitud(); i++) {
            try {
                listAsociados.add(tarjetas.Obtener(i).toString());
            } catch (Exception ex) {
                ex.printStackTrace();
            }
        }

        for (int i = 0; i < solicitudes.Longitud(); i++) {
            try {
                listSolicitudes.add(solicitudes.Obtener(i).toString());
            } catch (Exception ex) {
                ex.printStackTrace();
            }
        }


        for (int i = 0; i < materiales.Longitud(); i++) {
            try {
                listMateriales.add(materiales.Obtener(i).toString());
            } catch (Exception ex) {
                ex.printStackTrace();
            }
        }



    }

